mapper.update:
update largeclassification set LCALS_ID=LCALS_ID

	, lcals_name = #{LCALS_NAME}


	, lcals_intrcn = #{LCALS_INTRCN}


	, lcals_level = #{LACLS_LEVEL}


	, lcals_yn = #{LCALS_YN}

where lcals_id = #{ID}

mapper.select:
select lcals_log_id, lcals_id, lcals_name, lcals_intrcn, lcals_level, lcals_log_division, lcals_log_performer, lcals_log_date from largeclassificationLog where 1=1
lcals_log_id = #{LCALS_LOG_ID}
lcals_id = #{LCALS_ID}

mapper.update:
update MenuDetail set menu_id = menu_id

	, menu_name = #{MENU_NAME}


	, menu_link = #{MENU_LINK}


	, menu_content = #{MENU_CONTENT}


	, menu_yn = #{MENU_YN}

mapper.select:
select menu_id, lcals_id, menu_name, menu_file, menu_eventn, menu_link, menu_content, menu_yn
from MenuDetail
where menu_id = #{MENU_ID}

select menu_id, lcals_id, menu_name, menu_file, menu_eventn, menu_link, menu_content, menu_yn
from MenuDetaillog
where menu_log_id = #{MENU_LOG_ID}
